import React , {useEffect, useState, useCallback} from "react";
import loadingUrl from './img/loading.svg'
function App() {
  const [urlData, setUrlData] = useState({})
  const [errorMessage, setErrorMessage] = useState("")
  const [tabUrl, setTabUrl] = useState("")
  const [loading, setLoading] = useState(true)

   useEffect(() => {
     setLoading(true)
      browser.tabs.query({currentWindow: true, active: true})
        .then((tabs) => {
            const x = new URL(tabs[0].url).hostname
            console.log(x);
            setTabUrl(x)
      })
    },[setUrlData]
  )

   const fetchApi = useCallback(async () => { 
    try{
      setLoading(true)
      const response = await fetch(`http://ip-api.com/json/${tabUrl}`)
      if(tabUrl === "") throw new Error ("url is emptyðŸ¤·â€â™‚ï¸")
      const jsonResponse = await response.json();
      if(jsonResponse.status === "fail" ) throw new Error ("something went wrong")
      setUrlData(jsonResponse)
      }
    catch(err){
      setErrorMessage(err.message)
    }
    setLoading(false)
  },[tabUrl])
  useEffect(() => {fetchApi()}, [fetchApi])
  console.log("red")
  return (
    <div className="app">
      {loading ? <img src={ loadingUrl} /> : urlData.country ?? errorMessage}
    </div>
  );
}

export default App;
