import React , {useEffect, useState, useCallback} from "react";
function App() {
  const [urlData, setUrlData] = useState({})
  const [errorMessage, setErrorMessage] = useState("")
   const fetchApi = useCallback(async () => { 
    try{
      const response = await fetch(`http://ip-api.com/json/${window.location.hostname}`)
      const jsonResponse = await response.json();
      if(jsonResponse.status === "fail" ) throw "something went wrong"
      setUrlData(jsonResponse)
    }
    catch(err){
      console.log(err)
      setErrorMessage(err.message)
    }


  },[])
  useEffect(() => {fetchApi()}, [])
  return (
    <div className="app">
      {urlData.country ?? errorMessage}
    </div>
  );
}

export default App;
