import React , {useEffect, useState, useCallback} from "react";
function App() {
  const [urlData, setUrlData] = useState({})
  const [errorMessage, setErrorMessage] = useState("")
  function getPage(){
    browser.tabs.query({currentWindow: true, active: true})
      .then((tabs) => {
        return tabs[0].url
    })
  }

   const fetchApi = useCallback(async () => { 
    console.log(getPage)
    try{
      const response = await fetch(`http://ip-api.com/json/${window.location.host.toString()}`)
      const jsonResponse = await response.json();
      if(jsonResponse.status === "fail" ) throw new Error ("something went wrong")
      setUrlData(jsonResponse)
    }
    catch(err){
      setErrorMessage(err.message)
    }


  },[])
  useEffect(() => {fetchApi()}, [fetchApi])
  return (
    <div className="app">
      {urlData.country ?? getPage()}
    </div>
  );
}

export default App;
